import { Col, Container, Row, Form, Button } from 'react-bootstrap';
import Card from 'react-bootstrap/Card';
import './App.css';
import { useState } from 'react';

function App() {
  const nums = [1, 2, 3, 4, 5, 6, 7, 8, 9, 0, "."]
  const signs = ['+', '-', '*', '/']
  const [textInput, setTextInput] = useState('')
  const [result, setResult] = useState('')

  const handleNumberClick = (number) => {
    setTextInput(prev => prev + String(number));
  };

  const handleOperatorClick = (operator) => {
    setTextInput(prev => prev + operator);
  };

  const calculateResult = () => {
    try {

      const calculatedResult = eval(textInput);
      setResult(calculatedResult);
    } catch (error) {
      setResult('Error');
    }
  };

  return (
    <div className="App">
      <Container>
        <Row>
          <Col xs={12} lg={6} xl={3}> 
            <Card>
              <Card.Body id='calcContainer'>
                <Form.Control
                  value={textInput}
                  disabled
                  className={'mb-2'}
                  size="lg"
                  type="text"
                  placeholder="0"
                />
                <div className={''}>
                  <Row>
                    <Col xs={9}>
                      <Row>
                        {nums.map((b) => 
                          <Col xs={4} className={'d-flex mb-1 p-0 m-0 justify-content-center'}>
                            <Button
                              onClick={() => handleNumberClick(b)}
                              className={''}
                              size={'lg'}
                              variant={'dark'}
                              key={b}
                            >
                              {b}
                            </Button>
                          </Col>
                        )}
                      </Row>
                    </Col>
                    <Col xs={3} className='h-100'>
                      {signs.map((b) => 
                        <Button
                          onClick={() => handleOperatorClick(b)}
                          size={'lg'}
                          variant={'warning'}
                          className={'mb-1 lh-1'}
                          key={b}
                        >
                          {b}
                        </Button>
                      )}
                      <Button
                        onClick={calculateResult}
                        size={'lg'}
                        variant={'success'}
                        className={'mb-1 lh-1'}
                      >
                        =
                      </Button>
                    </Col>
                  </Row>
                </div>  
              </Card.Body>
            </Card>
          </Col>
        </Row>
        <Row>
          <Col xs={12} lg={6} xl={3}>
            <Card>
              <Card.Body>
                <h2>Result: {result}</h2>
              </Card.Body>
            </Card>
          </Col>
        </Row>
      </Container>
    </div>
  );
}

export default App;
